$arMacros = array();

foreach ($arContentIBlock as $iBlockXmlId => $arFields):

$iblockCode = $moduleCode . '_' . $iBlockXmlId . '_' . WIZARD_SITE_ID;
$iblockXMLFile = WIZARD_SERVICE_RELATIVE_PATH . "/xml/" . LANGUAGE_ID . "/bitronic2_{$iBlockXmlId}.xml"; 

$rsIBlock = CIBlock::GetList(array(), array("XML_ID" => $iblockCode, "TYPE" => $iblockType));
$iblockID = false; 
if ($arIBlock = $rsIBlock->Fetch())
{
	$iblockID = $arIBlock["ID"]; 
	if (WIZARD_INSTALL_DEMO_DATA)
	{
		CIBlock::Delete($arIBlock["ID"]); 
		$iblockID = false; 
	}
}

if($iblockID == false)
{
	$permissions = Array(
			"1" => "X",
			"2" => "R"
		);
	$dbGroup = CGroup::GetList($by = "", $order = "", Array("STRING_ID" => "content_editor"));
	if($arGroup = $dbGroup -> Fetch())
	{
		$permissions[$arGroup["ID"]] = 'W';
	};
	$iblockID = WizardServices::ImportIBlockFromXML(
		$iblockXMLFile,
		$iblockCode,
		$iblockType,
		WIZARD_SITE_ID,
		$permissions
	);

	if ($iblockID < 1)
		return;
	
	//IBlock fields
	$iblock = new CIBlock;
	$arFields = Array(
		"ACTIVE" => "Y",
		"FIELDS" => $arFields,
		"CODE" => $iblockCode,
		"XML_ID" => $iblockCode,
		"NAME" => $iblock->GetArrayByID($iblockID, "NAME") . ' (' . WIZARD_SITE_ID . ')'
	);
	
	$iblock->Update($iblockID, $arFields);
}
else
{
	$arSites = array(); 
	$db_res = CIBlock::GetSite($iblockID);
	while ($res = $db_res->Fetch())
		$arSites[] = $res["LID"]; 
	if (!in_array(WIZARD_SITE_ID, $arSites))
	{
		$arSites[] = WIZARD_SITE_ID;
		$iblock = new CIBlock;
		$iblock->Update($iblockID, array("LID" => $arSites));
	}
}
$dbSite = CSite::GetByID(WIZARD_SITE_ID);
if($arSite = $dbSite -> Fetch())
	$lang = $arSite["LANGUAGE_ID"];
if(strlen($lang) <= 0)
	$lang = "ru";
	
	//SET MESSAGES
	$langCode = strtoupper($iBlockXmlId);
	$arMessages = array(
		'ELEMENT_NAME' 		=> GetMessage($langCode . '-ELEMENT_NAME'),
		'ELEMENTS_NAME' 	=> GetMessage($langCode . '-ELEMENTS_NAME'),
		'ELEMENT_ADD' 		=> GetMessage($langCode . '-ELEMENT_ADD'),
		'ELEMENT_EDIT' 		=> GetMessage($langCode . '-ELEMENT_EDIT'),
		'ELEMENT_DELETE' 	=> GetMessage($langCode . '-ELEMENT_DELETE'),
		'SECTION_NAME' 		=> GetMessage($langCode . '-SECTION_NAME'),
		'SECTIONS_NAME' 	=> GetMessage($langCode . '-SECTIONS_NAME'),
		'SECTION_ADD' 		=> GetMessage($langCode . '-SECTION_ADD'),
		'SECTION_EDIT' 		=> GetMessage($langCode . '-SECTION_EDIT'),
		'SECTION_DELETE' 	=> GetMessage($langCode . '-SECTION_DELETE'),
	);
	CIBlock::SetMessages($iblockID, $arMessages);
	
WizardServices::IncludeServiceLang("news.php", $lang);
CUserOptions::SetOption("form", "form_element_".$iblockID, array ( 'tabs' => 'edit1--#--'.GetMessage("WZD_OPTION_NEWS_1").'--,--ACTIVE--#--'.GetMessage("WZD_OPTION_NEWS_2").'--,--ACTIVE_FROM--#--'.GetMessage("WZD_OPTION_NEWS_3").'--,--NAME--#--'.GetMessage("WZD_OPTION_NEWS_5").'--,--CODE--#--'.GetMessage("WZD_OPTION_NEWS_6").'--,--PREVIEW_PICTURE--#--'.GetMessage("WZD_OPTION_NEWS_11").'--,--DETAIL_PICTURE--#--'.GetMessage("WZD_OPTION_NEWS_12").'--,--PREVIEW_TEXT--#--'.GetMessage("WZD_OPTION_NEWS_8").'--,--DETAIL_TEXT--#--'.GetMessage("WZD_OPTION_NEWS_10").'--;--', ));

CUserOptions::SetOption("list", "tbl_iblock_list_".md5($iblockType.".".$iblockID), array ( 'columns' => 'NAME,ACTIVE,DATE_ACTIVE_FROM', 'by' => 'timestamp_x', 'order' => 'desc', 'page_size' => '20', ));

$arMacros[$langCode . '_IBLOCK_ID'] = $iblockID;

endforeach;

CWizardUtil::ReplaceMacrosRecursive(WIZARD_SITE_PATH . '/company/', $arMacros);

